import { FeatureService } from '../services/feature.service';
import { VehicleService } from './../services/make.service';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-vehicle-form',
  templateUrl: './vehicle-form.component.html',
  styleUrls: ['./vehicle-form.component.css']
})
export class VehicleFormComponent implements OnInit{
  makes: any;
  models: any;
  vehicle: any = {};
  features: any;
  constructor(
    private makeService: VehicleService,
    private featureService: FeatureService) {


  }
  ngOnInit(): void {
    this.makeService.getMakes().subscribe(makes=>{
      this.makes = makes;});
      this.featureService.getFeatures().subscribe(features => this.features = features)
  }
  onMakeChange(){
    console.log("Vehicle",this.vehicle);
    var selectedMake = this.makes.find((m: { id: any; }) => m.id == this.vehicle.make);
    this.models = selectedMake ? selectedMake.models : [];
  }
}
